import React, { useState } from "react";
import Clock from "./Clock"; // Import the Clock component

function Login() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [randomStory, setRandomStory] = useState("");

  const funnyStories = [
    "You must be the change you wish to see in the world.",
    "Success is walking from failure to failure with no loss of enthusiasm.",
    "To be yourself in a world that is constantly trying to make you something else is the greatest accomplishment.",
    "The greatest glory in living lies not in never falling, but in rising every time we fall.",
    "Believe you can and you're halfway there.",
    "Life is what happens when you're busy making other plans.",
    "The only way to do great work is to love what you do.",
    "The mind is everything. What you think you become.",
    "Happiness is not something ready-made. It comes from your own actions.",
    "To find yourself, think for yourself.",
    "The only thing necessary for the triumph of evil is for good men to do nothing.",
    "The only real mistake is the one from which we learn nothing.",
    "It does not matter how slowly you go as long as you do not stop.",
    "Life is really simple, but we insist on making it complicated.",
    "The purpose of our lives is to be happy.",
    "In three words I can sum up everything I've learned about life: it goes on.",
    "Be yourself; everyone else is already taken.",
    "I can't change the direction of the wind, but I can adjust my sails to always reach my destination.",
    "The only limit to our realization of tomorrow will be our doubts of today.",
    "You are never too old to set another goal or to dream a new dream.",
    "Success is not final, failure is not fatal: It is the courage to continue that counts.",
    "The best way to predict the future is to create it.",
    "To live is the rarest thing in the world. Most people exist, that is all.",
    "Your time is limited, don't waste it living someone else's life.",
    "The journey of a thousand miles begins with one step.",
    "Our greatest glory is not in never falling, but in rising every time we fall.",
    "The future belongs to those who believe in the beauty of their dreams.",
    "The only thing standing between you and your goal is the story you keep telling yourself as to why you can't achieve it.",
    "The only thing worse than being blind is having sight but no vision.",
    "Do not go where the path may lead, go instead where there is no path and leave a trail."
  ];

  const getRandomStory = () => {
    const randomIndex = Math.floor(Math.random() * funnyStories.length);
    return funnyStories[randomIndex];
  };

  const handleLogin = () => {
    // Simulate a successful login
    setIsLoggedIn(true);
    // Get a random funny story
    const story = getRandomStory();
    setRandomStory(story);
  };

  const handleBack = () => {
    // Simulate going back to the login page
    setIsLoggedIn(false);
    // Clear the random story
    setRandomStory("");
  };

  const handleRefresh = () => {
    // Generate a new random story and update the state
    const newStory = getRandomStory();
    setRandomStory(newStory);
  };

  return (
    <div>
      {!isLoggedIn ? (
        <button
          style={{
            backgroundColor: "#4caf50", // Green background
            color: "white", // White text color
            border: "none", // Remove border
            padding: "10px 20px", // Padding
            cursor: "pointer", // Add cursor on hover
            borderRadius: "5px", // Rounded corners
            marginRight: "10px", // Margin between buttons
            fontSize: "18px" // Larger font size
          }}
          onClick={handleLogin}
        >
          Login
        </button>
      ) : (
        <div>
          <button
            style={{
              backgroundColor: "#008CBA", // Blue background for back button
              color: "white",
              border: "none",
              padding: "10px 20px",
              cursor: "pointer",
              borderRadius: "5px",
              marginRight: "10px",
              fontSize: "18px" // Larger font size
            }}
            onClick={handleBack}
          >
            Back
          </button>
          <button
            style={{
              backgroundColor: "#008CBA", // Blue background for refresh button
              color: "white",
              border: "none",
              padding: "10px 20px",
              cursor: "pointer",
              borderRadius: "5px",
              fontSize: "18px" // Larger font size
            }}
            onClick={handleRefresh}
          >
            Refresh
          </button>
          <p
            style={{
              fontSize: "24px", // Larger font size for text
              marginBottom: "10px" // Margin bottom added for spacing
            }}
          ></p>
          <p
            style={{
              fontSize: "20px" // Larger font size for story text
            }}
          >
            {randomStory}
          </p>
          <div style={{ marginTop: "20px" }}>
            <Clock /> {/* Include the Clock component here */}
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
